package compte;/** Representation de la carte guichet du client */public class Carte{    /** Constructor     *     *  @param number numero de la carte     */    public Carte(int number)    {        this.number = number;    }        /** Accessor du numero     *     *  @return le numero de la carte     */    public int getNumber()    {        return number;    }        /** Numero de carte     */    private int number;}