package compte;/** Classe de base abstraite pour la representation des differents codes de transaction retournes par la banque. * La banque va creer les sous-classes appropriees. */public abstract class Etat{    /** Cree une reprensentation en String de cet etat pour l'impression.      *      *  @return representation string     */    public String toString()    {        if (isSuccess())            return "SUCCES";        else if (isInvalidPIN())            return "NIP INVALIDE";        else            return "ECHEC " + getMessage();    }        /** Verifie si l'etat est succes     *     *  @return true si l'etat est succes     */    public abstract boolean isSuccess();        /** Verifie si l'etat est succes PIN invalide     *     *  @return true si l'etat est PIN invalide     */    public abstract boolean isInvalidPIN();        /** Accessor message decrivant l'etat (utilise si l'etat n'est pas succes)     *     *  @return description du probleme     */    public abstract String getMessage();}