package guichet.physique;import compte.Argent;import simulation.Simulation;/** Gestionnaire du distributeur d'argent. Dans un vrai guichet, ceci gere un composant physique;  * dans cette simulation, ca utilise les classes dans le package simulation. */ public class DistributeurArgent{    /** Constructor     *     *  @param log le journal dans lequel on enregistre la distribution d'argent     */    public DistributeurArgent(Journal log)    {        this.log = log;                cashOnHand = new Argent(0);    }        /** Initialise le montant d'argent dans le distributeur     *     *  @param initialCash le montant d'argent dans le distributeur     */    public void setInitialCash(Argent initialCash)    {        cashOnHand = initialCash;    }        /** Verifie s'il y a assez d'argent pour satisfaire une requete     *     *  @param amount Le montant d;argent desire par le client     *  @return true si au moins ce montant est disponible     */    public boolean checkCashOnHand(Argent amount)    {        return amount.lessEqual(cashOnHand);    }        /** Distribue l'argent au client     *     *  @param amount le montant d'argent a distribuer     *     *  Precondition: amount est <= argent disponible     */    public void dispenseCash(Argent amount)    {        cashOnHand.subtract(amount);                Simulation.getInstance().dispenseCash(amount);                log.logCashDispensed(amount);    }    /** Log Le journal ou on enregistre la distribution d'argent     */     private Journal log;        /** Argent disponible     */    private Argent cashOnHand;}