package guichet.physique;import simulation.Simulation;/** Gestionnaire de la fente d'enveloppe.  Dans un vrai guichet, ceci gere un composant physique;  * dans cette simulation, ca utilise les classes dans le package simulation. */ public class FenteEnveloppe{    /** Constructor     *     *  @param log le journal dans lequel on enregistre la reception denveloppe     */    public FenteEnveloppe(Journal log)    {        this.log = log;    }        /** Accpet une enveloppe du client.     *     *  @exception ConsoleClient.Cancelled si l'operation a depasse      *  le temps alloue ou le client a appuyer sur ANNULER      */    public void acceptEnvelope() throws ConsoleClient.Cancelled    {        boolean inserted = Simulation.getInstance().acceptEnvelope();        if (inserted)            log.logEnvelopeAccepted();        else            throw new ConsoleClient.Cancelled();    }        /** le journal dans lequel on enregistre la reception denveloppe     */    private Journal log;}