 package guichet.transaction;import compte.InformationCompte;import compte.Carte;import compte.Message;import compte.Argent;import compte.Recu;import compte.Etat;import guichet.Guichet;import guichet.Session;import guichet.physique.*;/** Representation d'une transaction de demande de solde */public class Demande extends Transaction{    /** Constructor     *     *  @param atm le guichet utilise pour communiquer avec le client     *  @param session la session dans laquelle la transaction est realisee     *  @param card la carte client     *  @param pin le NIP entre par le client     */    public Demande(Guichet atm, Session session, Carte card, int pin)    {        super(atm, session, card, pin);    }        /** Recupere des details de la transaction avec le client     *     *  @return message a la banque pour initier cette transaction      *  @exception ConsoleClient.Cancelled si le client annule la transaction     */    protected Message getSpecificsFromCustomer() throws ConsoleClient.Cancelled    {        from = atm.getCustomerConsole().readMenuChoice(            "Compte a considerer pour la demande",            InformationCompte.ACCOUNT_NAMES);        return new Message(Message.INQUIRY,                         card, pin, serialNumber, from, -1, new Argent(0));    }        /** Complete une transaction approuvee     *     *  @return recu a etre imprimer pour cette transaction     */    protected Recu completeTransaction()    {        return new Recu(this.atm, this.card, this, this.balances) {            {                detailsPortion = new String[2];                detailsPortion[0] = "DEMANDE Du: " +                                     InformationCompte.ACCOUNT_ABBREVIATIONS[from];                detailsPortion[1] = "";            }        };    }        /** Compte a considerer pour la demande     */    private int from;    }