package guichet.transaction;import compte.InformationCompte;import compte.Carte;import compte.Message;import compte.Argent;import compte.Recu;import compte.Etat;import guichet.Guichet;import guichet.Session;import guichet.physique.*;/** Representation d'une transaction de transfert */public class Transfert extends Transaction{	/** Constructor    *    *  @param atm le guichet utilise pour communiquer avec le client    *  @param session la session dans laquelle la transaction est realisee    *  @param card la carte client    *  @param pin le NIP entre par le client    */    public Transfert(Guichet atm, Session session, Carte card, int pin)    {        super(atm, session, card, pin);    }        /** Recupere des details de la transaction avec le client    *    *  @return message a la banque pour initier cette transaction     *  @exception ConsoleClient.Cancelled si le client annule la transaction    */    protected Message getSpecificsFromCustomer() throws ConsoleClient.Cancelled    {        from = atm.getCustomerConsole().readMenuChoice(            "Compte duquel transferer",            InformationCompte.ACCOUNT_NAMES);        to = atm.getCustomerConsole().readMenuChoice(            "Compte vers lequel transferer",            InformationCompte.ACCOUNT_NAMES);        amount = atm.getCustomerConsole().readAmount("Montant a transferer");                return new Message(Message.TRANSFER,                         card, pin, serialNumber, from, to, amount);    }        /** Complete une transaction approuvee    *    *  @return recu a etre imprimer pour cette transaction    */    protected Recu completeTransaction()    {        return new Recu(this.atm, this.card, this, this.balances) {            {                detailsPortion = new String[2];                detailsPortion[0] = "TRANSFERER Du: " +                                     InformationCompte.ACCOUNT_ABBREVIATIONS[from] +                                    " Vers: " +                                     InformationCompte.ACCOUNT_ABBREVIATIONS[to] ;                detailsPortion[1] = "Montant: " + amount.toString();            }        };    }        /** Compte duquel on transfere     */    private int from;        /** Compte vers lequel on transfere     */    private int to;        /** AMontant d'argent a trannsferer     */    private Argent amount;           }