package simulation;import java.awt.*;import java.awt.event.*;import compte.Argent;/** Simule le distributeur d'argent */class SimDistributeurArgent extends Panel{    /** Constructor     */    SimDistributeurArgent()    {        setLayout(new GridLayout(1,1));        label = new Label("$XXX.XX", Label.CENTER);        label.setFont(new Font("SansSerif", Font.PLAIN, 24));        label.setForeground(new Color(0, 64, 0));        add(label);        label.setVisible(false);    }        /** Anime la distribution d'argent au client     *     *  @param amount Le montant distribue     *     *  Precondition: amount est <= argent disponible     */    public void animateDispensingCash(Argent amount)    {        label.setText(amount.toString());        for (int size = 3; size <= 24; size += 1)        {             label.setFont(new Font("SansSerif", Font.PLAIN, size));            label.setVisible(true);            try            {                 Thread.sleep(250);            }            catch (InterruptedException e)            { }            label.setVisible(false);        }    }        /** Etiquette representant le l'argent distribue     */    private Label label;}                                       