package simulation;import java.awt.*;import java.awt.event.*;/** Simule l'imprimeur de recu */class SimImprimeurRecu extends Panel{    /** Constructor     */    SimImprimeurRecu()    {        setLayout(new BorderLayout(5, 5));                        printArea =             new TextArea("", PanneauGuichet.PRINTABLE_LINES, PanneauGuichet.PRINTABLE_CHARS,                         TextArea.SCROLLBARS_VERTICAL_ONLY);        printArea.setBackground(Color.white);        printArea.setForeground(Color.black);        printArea.setFont(new Font("Monospaced", Font.PLAIN, 12));        printArea.setEditable(false);        add(printArea, BorderLayout.SOUTH);                                    Panel buttonPanel = new Panel();        buttonPanel.setLayout(new GridLayout(1,1));        take = new Button("Prendre Recu");        buttonPanel.add(take);        add(buttonPanel, BorderLayout.NORTH);        take.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                printArea.setText("");                take.setVisible(false);            }        });                take.setVisible(false);    }        /** Imprime une ligne sur le recu     *     *  @param text la ligne a imprimer     */    void println(String text)    {        printArea.append(text + '\n');        try        {             Thread.sleep(1 * 1000);        }        catch (InterruptedException e)        { }       take.setVisible(true);    }            /** Simule impression recu     */    private TextArea printArea;        /** Boutton pour permettre au client de prendre recu     */    private Button take;}                                       